\n    },\n    \"hestia::hestia_blog_sidebar_layout\": {\n        \"value\": \"full-width\",\n        \"type\": \"theme_mod\",\n        \"user_id\": 1,\n        \"date_modified_gmt\": \"2019-05-22 23:55:16\"\n    },\n    \"hestia::background_color\": {\n        \"value\": \"#0a0a0a\",\n        \"type\": \"theme_mod\",\n        \"user_id\": 1,\n        \"date_modified_gmt\": \"2019-05-22 23:56:16\"\n    },\n    \"hestia::hestia_slider_type\": {\n        \"value\": \"parallax\",\n        \"type\": \"theme_mod\",\n        \"user_id\": 1,\n        \"date_modified_gmt\": \"2019-05-22 23:56:16\"\n    },\n    \"hestia::hestia_big_title_background\": {\n        \"value\": \"\",\n        \"type\": \"theme_mod\",\n        \"user_id\": 1,\n        \"date_modified_gmt\": \"2019-05-22 23:56:16\"\n    },\n    \"hestia::hestia_big_title_hide\": {\n        \"value\": false,\n        \"type\": \"theme_mod\",\n        \"user_id\": 1,\n        \"date_modified_gmt\": \"2019-05-22 23:56:16\"\n    },\n    \"hestia::hestia_about_hide\": {\n        \"value\": true,\n        \"type\": \"theme_mod\",\n        \"user_id\": 1,\n        \"date_modified_gmt\": \"2019-05-22 23:56:16\"\n    },\n    \"hestia::hestia_blog_hide\": {\n        \"value\": true,\n        \"type\": \"theme_mod\",\n        \"user_id\": 1,\n        \"date_modified_gmt\": \"2019-05-22 23:56:16\"\n    },\n    \"hestia::hestia_contact_hide\": {\n        \"value\": true,\n        \"type\": \"theme_mod\",\n        \"user_id\": 1,\n        \"date_modified_gmt\": \"2019-05-22 23:56:16\"\n    },\n    \"hestia::hestia_features_hide\": {\n        \"value\": true,\n        \"type\": \"theme_mod\",\n        \"user_id\": 1,\n        \"date_modified_gmt\": \"2019-05-22 23:56:16\"\n    },\n    \"hestia::hestia_testimonials_hide\": {\n        \"value\": true,\n        \"type\": \"theme_mod\",\n        \"user_id\": 1,\n        \"date_modified_gmt\": \"2019-05-22 23:56:16\"\n    },\n    \"hestia::hestia_team_hide\": {\n        \"value\": true