(152, 'theme_mods_fagri', 'a:32:{i:0;b:0;s:29:\"hestia_contact_form_shortcode\";s:51:\"[wpforms id=\"28\" title=\"false\" description=\"false\"]\";s:28:\"ti_about_recommended_plugins\";a:1:{s:19:\"themeisle-companion\";s:7:\"visible\";}s:18:\"nav_menu_locations\";a:1:{s:7:\"primary\";i:4;}s:21:\"hestia_general_layout\";i:0;s:28:\"zerif_frontpage_was_imported\";s:9:\"not-zerif\";s:13:\"ti_prev_theme\";s:14:\"twentynineteen\";s:18:\"custom_css_post_id\";i:-1;s:24:\"hestia_feature_thumbnail\";s:84:\"http://localhost/portfolio/wordpress/wp-content/themes/hestia/assets/img/contact.jpg\";s:31:\"hestia_feature_thumbnail_buffer\";s:84:\"http://localhost/portfolio/wordpress/wp-content/themes/hestia/assets/img/contact.jpg\";s:11:\"custom_logo\";i:23;s:27:\"hestia_big_title_background\";s:71:\"http://localhost/portfolio/wordpress/wp-content/uploads/2019/07/COR.png\";s:22:\"hestia_big_title_title\";s:8:\"TOGETHER\";s:23:\"hestia_slider_alignment\";s:4:\"left\";s:21:\"hestia_big_title_text\";s:17:\"Aprendendo a amar\";s:16:\"background_color\";s:6:\"e5e5e5\";s:12:\"accent_color\";s:7:\"#8c1226\";s:23:\"hestia_features_content\";s:1027:\"[{\"icon_value\":\"fa-wechat\",\"color\":\"#e91e63\",\"text\":\"Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.\",\"link\":\"#\",\"text2\":\"undefined\",\"title\":\"Responsive\",\"subtitle\":\"undefined\",\"social_repeater\":\"undefined\",\"id\":\"social-repeater-5d1e4eb21b82b\",\"shortcode\":\"undefined\"},{\"icon_value\":\"fa-check\",\"color\":\"#00bcd4\",\"text\":\"Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.\",\"link\":\"#\",\"text2\":\"undefined\",\"title\":\"Quality\",\"subtitle\":\"undefined\",\"social_repeater\":\"undefined\",\"id\":\"social-repeater-5d1e4eb2a75a5\",\"shortcode\":\"undefined\"},{\"icon_value\":\"fa-support\",\"color\":\"#4caf50\",\"text\":\"Lorem ipsum dolor sit amet, c